import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

import * as api from "../../api/api.js";

import NavBar from "../NavBar/NavBar";
import ProjectPage from "../ProjectPage/ProjectPage";

import "./Dashboard.css";

const Dashboard = () => {
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/isUserAuth", {
      headers: {
        "x-access-token": localStorage.getItem("token"),
      },
    })
      .then((res) => res.json())
      .then((data) =>
        data.isLoggedIn ? console.log(data.username) : navigate("/login")
      );
  });

  const userID = localStorage.getItem("userID");
  console.log(userID);
  const [projects, setProjects] = useState([]);

  const loadProjects = async () => {
    const userID = localStorage.getItem("userID");
    const payload = {
      method: "GET",
    };

    try {
      const res = await fetch(
        `http://localhost:5000/${userID}/projects`,
        payload
      );
      const projects = await res.json();
      setProjects(projects);
      console.log(projects);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    loadProjects();
  }, []);

  const [curProject, setCurProject] = useState(projects[0]);

  useEffect(() => {
    setCurProject(projects[0]);
  }, [projects]);

  return (
    <div className="dashboard-wrapper">
      <NavBar projects={projects} setProject={setCurProject} />

      <ProjectPage project={curProject} />
    </div>
  );
};

export default Dashboard;
