import React, { useEffect, useState } from "react";

import "./BugsPage.css";

import Popup from "../CreateBug/CreateBug";
import DetailPopup from "../BugDetail/BugDetail";


const BugsPage = ({ bugs }) => {

    const [isLoaded, setIsLoaded] = useState(false);
    const [getBugs, setGetBugs] = useState([]);
    
    const [isOpen, setIsOpen] = useState(false);
    const [isDetail, setIsDetail] = useState(false);
 
    const togglePopup = () => {
        setIsOpen(!isOpen);
    }

    const toggleDetail = () => {
        setIsOpen(!isDetail);
    }

    useEffect(() => {
          fetch("http://localhost:5000/bugs")
            .then((res) => res.json())
            .then(
                (bug) => {
                    setIsLoaded(true);
                    setGetBugs(bug);
                }
            );
    });

    var index = 1;

    return (
        <div className="bugs-wrapper">
            {bugs ? (
                <div>
                    <h2>{bugs.name}</h2>
                    <p>Bugs reported:</p>
                    <nav>
                        <ol start={index}>
                            {getBugs.map(bug => (
                            <li key={bug.id}>
                                <button>
                                {bug._id} {' - '} {bug.name} {' - '} {bug.status}
                                </button>
                                <input className="viewDetail"
                                    type="button"
                                    value= {bug._id + " - " + bug.name + " - " + bug.status}
                                    onClick={toggleDetail}
                                />
                            </li>
                            ))}
                        </ol>
                    </nav>
                    {isDetail && <DetailPopup
                        content={<>
                            <b>Bug's detail</b>
                            {getBugs.map(bug => (
                                <li key={bug.id}>
                                    {bug._id} {bug.name} {bug.description} {bug.priority} {bug.deadline} {bug.status}
                                </li>
                            ))}
                            <button>Close</button>
                        </>}
                        handleClose={togglePopup}
                    />}
                    <input
                        type="button"
                        value="Report new bug"
                        onClick={togglePopup}
                    />
                    {isOpen && <Popup
                        content={<>
                            <b>Report a new bug</b>
                            <p>Enter details about new bugs here</p>
                            <button>Report</button>
                        </>}
                        handleClose={togglePopup}
                    />}
                </div>
            ) : (
                <h2>There is no bug</h2>
            )}
        </div>
    );
};

export default BugsPage;
