import React, { useEffect, useState } from "react";

import "./BugsPage.css";


const BugsPage = ({ bugs }) => {

    const [isLoaded, setIsLoaded] = useState(false);
    const [getBugs, setGetBugs] = useState([]);

    useEffect(() => {
          fetch("http://localhost:5000/bugs")
            .then((res) => res.json())
            .then(
                (bug) => {
                    setIsLoaded(true);
                    setGetBugs(bug);
                }
            );
    });

    return (
        <div className="bugs-wrapper">
            {bugs ? (
                <div>
                    <h2>{bugs.name}</h2>
                    <p>Bugs reported:</p>
                    <nav>
                        <ol>
                            {getBugs.map(bug => (
                            <li key={bug.id}>
                                <button>
                                {bug._id} {' - '} {bug.name} {' - '} {bug.status}
                                </button>
                            </li>
                            ))}
                        </ol>
                    </nav>
                </div>
            ) : (
                <h2>There is no bug</h2>
            )}
        </div>
    );
};

export default BugsPage;
